<?php 
  namespace common\taglib;  use think\template\TagLib;    class Model extends TagLib{            protected $tags   =  [                   'column'=>['where','app','model','rank','filed','assign','close'=>0],          'find'=>['where','app','model','rank','filed','assign','close'=>0],      ];      public function tagColumn($tag, $content){          if(!isset($tag['field']))$tag['field']='*';          if(!isset($tag['where']))$tag['where']='1';          return           "<?php ".              "if(\$cache=cache('". md5(json_encode($tag).'column')."')){".                      "\$".$tag['assign']."=\$cache;"              ."}else{"                      . "\$".$tag['assign'].'=appModel("'.$tag['app'].'","'.$tag['model'].'")'."->where(\"".$tag['where']."\")->limit(".$tag['limit'].")->order(\"".$tag['rank']."\")->column(\"".$tag['field']."\"); "              ."cache('".md5(json_encode($tag).'column')."',\$".$tag['assign'].",10);"                    ."}"                . "?>";      }      public function tagFind($tag, $content){          if(!isset($tag['field']))$tag['field']='*';          if(!isset($tag['where']))$tag['where']='1';          return           "<?php ".              "if(\$cache=cache('". md5(json_encode($tag).'find')."')){".                      "\$".$tag['assign']."=\$cache;"              ."}else{"                      . "\$".$tag['assign'].'=appModel("'.$tag['app'].'","'.$tag['model'].'")'."->where(\"".$tag['where']."\")->limit(".$tag['limit'].")->order(\"".$tag['rank']."\")->find(); "              ."cache('".md5(json_encode($tag).'find')."',\$".$tag['assign'].",10);"                    ."}"                . "?>";      }  }  