 * 时间: 2018-03-17 23:28:45
 */       class PHPExcel_RichText implements PHPExcel_IComparable  {            private $richTextElements;              public function __construct(PHPExcel_Cell $pCell = null)      {                   $this->richTextElements = array();                     if ($pCell !== null) {                           if ($pCell->getValue() != "") {                  $objRun = new PHPExcel_RichText_Run($pCell->getValue());                  $objRun->setFont(clone $pCell->getParent()->getStyle($pCell->getCoordinate())->getFont());                  $this->addText($objRun);              }                             $pCell->setValueExplicit($this, PHPExcel_Cell_DataType::TYPE_STRING);          }      }              public function addText(PHPExcel_RichText_ITextElement $pText = null)      {          $this->richTextElements[] = $pText;          return $this;      }              public function createText($pText = '')      {          $objText = new PHPExcel_RichText_TextElement($pText);          $this->addText($objText);          return $objText;      }              public function createTextRun($pText = '')      {          $objText = new PHPExcel_RichText_Run($pText);          $this->addText($objText);          return $objText;      }              public function getPlainText()      {                   $returnValue = '';                     foreach ($this->richTextElements as $text) {              $returnValue .= $text->getText();          }                     return $returnValue;      }              public function __toString()      {          return $this->getPlainText();      }              public function getRichTextElements()      {          return $this->richTextElements;      }              public function setRichTextElements($pElements = null)      {          if (is_array($pElements)) {              $this->richTextElements = $pElements;          } else {              throw new PHPExcel_Exception("Invalid PHPExcel_RichText_ITextElement[] array passed.");          }          return $this;      }              public function getHashCode()      {          $hashElements = '';          foreach ($this->richTextElements as $element) {              $hashElements .= $element->getHashCode();          }            return md5(              $hashElements .              __CLASS__          );      }              public function __clone()      {          $vars = get_object_vars($this);          foreach ($vars as $key => $value) {              if (is_object($value)) {                  $this->$key = clone $value;              } else {                  $this->$key = $value;              }          }      }  }  