 * 时间: 2018-03-15 19:07:22
 */       class PHPExcel_Writer_OpenDocument_Meta extends PHPExcel_Writer_OpenDocument_WriterPart  {            public function write(PHPExcel $pPHPExcel = null)      {          if (!$pPHPExcel) {              $pPHPExcel = $this->getParentWriter()->getPHPExcel();          }            $objWriter = null;          if ($this->getParentWriter()->getUseDiskCaching()) {              $objWriter = new PHPExcel_Shared_XMLWriter(PHPExcel_Shared_XMLWriter::STORAGE_DISK, $this->getParentWriter()->getDiskCachingDirectory());          } else {              $objWriter = new PHPExcel_Shared_XMLWriter(PHPExcel_Shared_XMLWriter::STORAGE_MEMORY);          }                     $objWriter->startDocument('1.0', 'UTF-8');                     $objWriter->startElement('office:document-meta');            $objWriter->writeAttribute('xmlns:office', 'urn:oasis:names:tc:opendocument:xmlns:office:1.0');          $objWriter->writeAttribute('xmlns:xlink', 'http:         $objWriter->writeAttribute('xmlns:dc', 'http:         $objWriter->writeAttribute('xmlns:meta', 'urn:oasis:names:tc:opendocument:xmlns:meta:1.0');          $objWriter->writeAttribute('xmlns:ooo', 'http:         $objWriter->writeAttribute('xmlns:grddl', 'http:         $objWriter->writeAttribute('office:version', '1.2');            $objWriter->startElement('office:meta');            $objWriter->writeElement('meta:initial-creator', $pPHPExcel->getProperties()->getCreator());          $objWriter->writeElement('dc:creator', $pPHPExcel->getProperties()->getCreator());          $objWriter->writeElement('meta:creation-date', date(DATE_W3C, $pPHPExcel->getProperties()->getCreated()));          $objWriter->writeElement('dc:date', date(DATE_W3C, $pPHPExcel->getProperties()->getCreated()));          $objWriter->writeElement('dc:title', $pPHPExcel->getProperties()->getTitle());          $objWriter->writeElement('dc:description', $pPHPExcel->getProperties()->getDescription());          $objWriter->writeElement('dc:subject', $pPHPExcel->getProperties()->getSubject());          $keywords = explode(' ', $pPHPExcel->getProperties()->getKeywords());          foreach ($keywords as $keyword) {              $objWriter->writeElement('meta:keyword', $keyword);          }                     $objWriter->startElement('meta:user-defined');          $objWriter->writeAttribute('meta:name', 'Company');          $objWriter->writeRaw($pPHPExcel->getProperties()->getCompany());          $objWriter->endElement();             $objWriter->startElement('meta:user-defined');          $objWriter->writeAttribute('meta:name', 'category');          $objWriter->writeRaw($pPHPExcel->getProperties()->getCategory());          $objWriter->endElement();            $objWriter->endElement();            $objWriter->endElement();            return $objWriter->getData();      }  }  